const i18n = require('../i18n/i18n');


class Logger {

    static log(message = '') {
        console.log(message);
    }

    static debug(message = '') {
        console.log(`${i18n.logger_debug} ${message}`);
    }

    static warn(message = '') {
        console.warn(`${i18n.logger_warn} ${message}`);
    }

    static error(message = '') {
        console.error(`${i18n.logger_error} ${message}`);
    }

}

module.exports = Logger;
